  contain: content;
}

.placeholder {
  position: absolute;
  inset: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, #f8f9fa 30%, #e9ecef 50%, #f8f9fa 70%);
  background-size: 200% 100%;
  border-radius: 8px;
  opacity: 1;
  animation: none;
  transition: opacity 300ms ease;
  will-change: opacity;
  z-index: 0;
}

/* ä»…å½“å®¹å™¨è¿›å…¥è§†å£æ—¶æ‰å¯ç”¨éª¨æ¶åŠ¨ç”» */
.container[data-inview="true"] .placeholder {
  animation: lazyimage-skeleton 2400ms ease-in-out infinite;
}

@keyframes lazyimage-skeleton {
  0% { background-position: 100% 50%; }
  50% { background-position: 0% 50%; }
  100% { background-position: 100% 50%; }
}

.placeholderImg {
  position: absolute;
  inset: 0;
  display: block;
  width: 100%;
  height: 100%;
  object-fit: cover;
  filter: blur(6px) brightness(0.96);
  transition: opacity 240ms ease;
  will-change: opacity;
  z-index: 0;
}

.img {
  position: absolute;
  inset: 0;
  z-index: 1;
  opacity: 0;
  /* ç§»é™¤é‡æ»¤é•œä»¥é™ä½æ …æ ¼åŒ–ä¸åˆæˆå¼€é”€ */
  /* filter: blur(6px) brightness(0.96); */
  transition: opacity 350ms cubic-bezier(0.4, 0, 0.2, 1);
  will-change: opacity;
  /* ç¡®ä¿å›¾ç‰‡åœ¨åŠ è½½æ—¶ä¹Ÿå æ®ç©ºé—´ */
  display: block;
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.placeholderImg {
  position: absolute;
  inset: 0;
  z-index: 0;
  opacity: 1;
  /* ç§»é™¤æ»¤é•œï¼Œä¿ç•™é™æ€å ä½ */
  /* filter: blur(6px) brightness(0.96); */
  /* å ä½ç¬¦æ·¡å‡ºæ—¶é—´ç¨é•¿ï¼Œé¿å…è¿‡æ—©æ¶ˆå¤± */
  transition: opacity 450ms cubic-bezier(0.4, 0, 0.6, 1);
  transition-delay: 100ms;
  will-change: opacity;
}

/* å½“ loaded=true æ—¶ï¼Œimg å¿«é€Ÿæ¸æ˜¾ */
.img[data-loaded="true"] {
  opacity: 1;
}

/* å½“ loaded=true æ—¶ï¼Œå ä½å…ƒç´ æ·¡å‡ºï¼ˆé€šè¿‡å®¹å™¨ data-loaded æ§åˆ¶ï¼‰*/
.container[data-loaded="true"] .placeholder,
.container[data-loaded="true"] .placeholderImg {
  opacity: 0;
}

/* åŠ è½½è¿›åº¦æŒ‡ç¤ºå™¨æ ·å¼ */
.loadingIndicator {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 2;
  opacity: 1;
  transition: opacity 240ms ease;
  will-change: opacity;
}

/* å½“å›¾ç‰‡åŠ è½½å®Œæˆæ—¶ï¼Œéšè—åŠ è½½æŒ‡ç¤ºå™¨ */
.container[data-loaded="true"] .loadingIndicator {
  opacity: 0;
  pointer-events: none;
}

/* é»˜è®¤åŠ è½½æŒ‡ç¤ºå™¨åŠ¨ç”» */
.defaultLoadingIndicator {
  width: 20px;
  height: 20px;
  border: 2px solid #e9ecef;
  border-top: 2px solid #6c757d;
  border-radius: 50%;
  animation: lazyimage-spin 1.5s ease-in-out infinite;
  opacity: 0.8;
}

@keyframes lazyimage-spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@media (prefers-reduced-motion: reduce) {
  .placeholder {
    animation: none;
  }
  .placeholderImg,
  .img,
  .loadingIndicator {
    transition: none;
  }
  .defaultLoadingIndicator {
    animation: none;
  }
}

