/**
 * 快速演示页面
 * 最简单的 LazyImage 使用示例
 */

import { LazyImage } from "../components/LazyImage";
import React from "react";

const QuickDemo: React.FC = () => {
  return (
    <div style={{ padding: "40px", textAlign: "center" }}>
      <h1>🚀 最简单的懒加载示例</h1>

      <div style={{ marginBottom: "40px" }}>
        <h2>单张图片懒加载</h2>
        <p>滚动页面，当图片进入视窗时开始加载</p>
      </div>

      {/* 占位内容，让用户需要滚动 */}
      <div style={{ height: "100vh", backgroundColor: "#f0f0f0", display: "flex", alignItems: "center", justifyContent: "center" }}>
        <div>
          <h3>向下滚动查看懒加载效果</h3>
          <p>👇</p>
        </div>
      </div>

      {/* 懒加载图片 */}
      <div style={{ margin: "40px 0" }}>
        <LazyImage
          src="https://picsum.photos/800/600?random=100"
          alt="懒加载示例图片"
          style={{
            width: "100%",
            maxWidth: "800px",
            height: "600px",
            objectFit: "cover",
            borderRadius: "8px",
            boxShadow: "0 4px 12px rgba(0,0,0,0.15)"
          }}
          enableLQIP={true}
          lqipWidth={20}
          lqipQuality={0.6}
          enableLQIPWorker={true}
          showLoadingIndicator={true}
          priority="medium"
          onLoad={() => console.log("✅ 图片加载成功")}
          onError={() => console.error("❌ 图片加载失败")}
        />
      </div>

      {/* 更多占位内容 */}
      <div style={{ height: "50vh", backgroundColor: "#e0e0e0", display: "flex", alignItems: "center", justifyContent: "center" }}>
        <div>
          <h3>继续滚动查看更多图片</h3>
          <p>👇</p>
        </div>
      </div>

      {/* 第二张懒加载图片 */}
      <div style={{ margin: "40px 0" }}>
        <LazyImage
          src="https://picsum.photos/800/600?random=200"
          alt="第二张懒加载图片"
          style={{
            width: "100%",
            maxWidth: "800px",
            height: "600px",
            objectFit: "cover",
            borderRadius: "8px",
            boxShadow: "0 4px 12px rgba(0,0,0,0.15)"
          }}
          enableLQIP={true}
          lqipWidth={20}
          lqipQuality={0.6}
          enableLQIPWorker={true}
          showLoadingIndicator={true}
          priority="low"
          onLoad={() => console.log("✅ 第二张图片加载成功")}
          onError={() => console.error("❌ 第二张图片加载失败")}
        />
      </div>

      {/* 代码示例 */}
      <div style={{
        margin: "40px auto",
        maxWidth: "800px",
        backgroundColor: "#f8f9fa",
        padding: "20px",
        borderRadius: "8px",
        textAlign: "left"
      }}>
        <h3>代码示例</h3>
        <pre style={{
          backgroundColor: "#fff",
          padding: "15px",
          borderRadius: "4px",
          overflow: "auto",
          fontSize: "14px"
        }}>
{`import { LazyImage } from 'vane-lazy-image';

<LazyImage
  src="https://example.com/image.jpg"
  alt="描述"
  style={{ width: "100%", height: "400px", objectFit: "cover" }}
  enableLQIP={true}
  lqipWidth={20}
  lqipQuality={0.6}
  enableLQIPWorker={true}
  showLoadingIndicator={true}
  priority="medium"
  onLoad={() => console.log("图片加载成功")}
  onError={() => console.error("图片加载失败")}
/>`}
        </pre>
      </div>

      {/* 底部占位 */}
      <div style={{ height: "50vh", backgroundColor: "#d0d0d0", display: "flex", alignItems: "center", justifyContent: "center" }}>
        <div>
          <h3>🎉 懒加载演示完成！</h3>
          <p>图片只有在进入视窗时才开始加载</p>
        </div>
      </div>
    </div>
  );
};

export default QuickDemo;
